CREATE DEFINER=`24SP_newmand3`@`localhost` FUNCTION `GetListIDs` (`username` VARCHAR(255)) RETURNS VARCHAR(255) CHARSET utf8mb4 DETERMINISTIC BEGIN
    DECLARE watchListID INT DEFAULT 0;
    DECLARE previouslyWatchedID INT DEFAULT 0;
    DECLARE result VARCHAR(255);
    SELECT watchList, previouslyWatched INTO watchListID, previouslyWatchedID
    FROM user
    WHERE username = username;
    SET result = CONCAT('Watch List ID: ', watchListID, ', Previously Watched ID: ', previouslyWatchedID);
    
    RETURN result;
END$$

