CREATE DEFINER=`24SP_newmand3`@`localhost` PROCEDURE `AddAnimeToList` (IN `list_id` INT, IN `anime_name` VARCHAR(255))   BEGIN
    IF EXISTS(SELECT name FROM anime WHERE name = anime_name) THEN
        IF EXISTS(SELECT id FROM animeList WHERE id = list_id) THEN
            VALUES (list_id, anime_name, (SELECT IFNULL(MAX(listIndex), 0) + 1 FROM animeListItem WHERE listID = list_id));
            SELECT 'Anime added to list successfully' AS message;
        ELSE
            SELECT 'List ID does not exist' AS message;
        END IF;
    ELSE
        SELECT 'Anime does not exist' AS message;
    END IF;
END$$

CREATE DEFINER=`24SP_newmand3`@`localhost` PROCEDURE `CreateAndAssignLists` (IN `user_username` VARCHAR(255))   BEGIN
    DECLARE existing_watchList INT;
    DECLARE existing_previouslyWatched INT;
    

    SELECT watchList, previouslyWatched INTO existing_watchList, existing_previouslyWatched
    FROM user
    WHERE username = user_username;
    

    IF existing_watchList IS NULL THEN
        UPDATE user SET watchList = LAST_INSERT_ID()
        WHERE username = user_username;
    END IF;
    
    IF existing_previouslyWatched IS NULL THEN
        UPDATE user SET previouslyWatched = LAST_INSERT_ID()
        WHERE username = user_username;
    END IF;

    SELECT username, watchList, previouslyWatched FROM user WHERE username = user_username;
END$$
